import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom"; // Import useLocation from react-router-dom

const Header = () => {
  const location = useLocation(); // Get current location
  const [activeText, setActiveText] = useState(""); // Initialize activeText state

  useEffect(() => {
    // Set active text based on the current route
    switch (location.pathname) {
      case "/":
        setActiveText("Home");
        break;
      case "/about":
        setActiveText("About");
        break;
      case "/solution":
        setActiveText("Solution");
        break;
      case "/contact":
        setActiveText("Contact");
        break;
      default:
        setActiveText("");
    }
  }, [location.pathname]); // Trigger effect when location.pathname changes
  const handleClick = (text) => {
    setActiveText(text);
    if (text === "Home") {
      window.location.href = "/"; // Redirect to home route
    } else {
      window.location.href = "/" + text.toLowerCase(); // Redirect to other routes
    }
  };

  const handleMouseEnter = (text) => {
    setActiveText(text);
  };



  return (
    <div className="bg-[#222] py-[28px]">
      <div className="container-class ">
        <div className="flex items-center justify-between">
          <div>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="45"
              height="46"
              viewBox="0 0 45 46"
              fill="none"
            >
              {/* SVG Path */}
            </svg>
          </div>
          <div className="flex items-center gap-[50px]">
            <p
              className={`text-white text-[18px] font-normal capitalize cursor-pointer ${
                activeText === "Home" ? "hover:text-[#00D588]" : ""
              }`}
              onMouseEnter={() => handleMouseEnter("Home")}
              onClick={() => handleClick("Home")}
            >
              Home
            </p>
            <p
              className={`text-white text-[18px] font-normal capitalize cursor-pointer ${
                activeText === "About" ? "hover:text-[#00D588]" : ""
              }`}
              onMouseEnter={() => handleMouseEnter("About")}
              onClick={() => handleClick("About")}
            >
              About
            </p>
            <p
              className={`text-white text-[18px] font-normal capitalize cursor-pointer ${
                activeText === "Solution" ? "hover:text-[#00D588]" : ""
              }`}
              onMouseEnter={() => handleMouseEnter("Solution")}
              onClick={() => handleClick("Solution")}
            >
              Solution
            </p>
            <p
              className={`text-white text-[18px] font-normal capitalize cursor-pointer ${
                activeText === "Contact" ? "hover:text-[#00D588]" : ""
              }`}
              onMouseEnter={() => handleMouseEnter("Contact")}
              onClick={() => handleClick("Contact")}
            >
              Contact
            </p>
          </div>
          <div>
            <button
              type="button"
              className="text-[#004862] bg-[#00D588] py-[8px] px-[32px] font-[400] rounded-[6px] text-[16px]"
            >
              Signup
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
